/*
 * Get a random number from 0 to (n-1) inclusive.
 */
uint32_t roll_die(uint32_t n)
{
  uint32_t r = random() % n;
  return r;
}

uint32_t gen_random_bit()
{
  uint32_t r = random() % 2;
  return r;
}

uint32_t gen_random_bit_biased()
{
  uint32_t r = random() % 4;
  if(r <= 2) {
	 return 0;
  } else {
	 return 1;
  }
}



