/**
 * Convert diagonals to columns.
 */
void norx_state_permute(norx_diff_state_t* S)
{
  WORD_T x[16] = {0};

  // extract columns
  x[0 ] = S->lane[ZERO].a;
  x[4 ] = S->lane[ZERO].b;
  x[8 ] = S->lane[ZERO].c;
  x[12] = S->lane[ZERO].d;

  x[1 ] = S->lane[ONE].a;
  x[5 ] = S->lane[ONE].b;
  x[9 ] = S->lane[ONE].c;
  x[13] = S->lane[ONE].d;

  x[2 ] = S->lane[TWO].a;
  x[6 ] = S->lane[TWO].b;
  x[10] = S->lane[TWO].c;
  x[14] = S->lane[TWO].d;

  x[3 ] = S->lane[THREE].a;
  x[7 ] = S->lane[THREE].b;
  x[11] = S->lane[THREE].c;
  x[15] = S->lane[THREE].d;

  // transform diagonals to columns
  S->lane[ZERO].a = x[ 0];
  S->lane[ZERO].b = x[ 5];
  S->lane[ZERO].c = x[10];
  S->lane[ZERO].d = x[15];

  S->lane[ONE].a = x[ 1];
  S->lane[ONE].b = x[ 6];
  S->lane[ONE].c = x[11];
  S->lane[ONE].d = x[12];

  S->lane[TWO].a = x[ 2];
  S->lane[TWO].b = x[ 7];
  S->lane[TWO].c = x[ 8];
  S->lane[TWO].d = x[13];

  S->lane[THREE].a = x[ 3];
  S->lane[THREE].b = x[ 4];
  S->lane[THREE].c = x[ 9];
  S->lane[THREE].d = x[14];

}

