void marx_init_diff_trail(differential_3d_t T[2][NROUNDS])
{
  for(uint32_t i = 0; i < 2; i++) {
	 for(uint32_t j = 0; j < NROUNDS; j++) {
		T[i][j].dx = 0; 
		T[i][j].dy = 0;
		T[i][j].dz = 0;
		T[i][j].p = 0.0;
		T[i][j].log2p = LOG0;;
	 }
  }
}


