#endif

	 uint32_t dyy = diff[n-1].dx; // dy_{i} = dx_{i-1}
	 uint32_t dxx = dy ^ dyy ^ LROT(dx, lrot_const_u); // dx_{i+1} = gamma ^ dx_{i-1} ^ (alpha <<< 2)

	 double p = 1.0;
	 for(int i = 0; i < n; i++) {
		p *= diff[i].p;
	 }
	 p *= pn;
	 diff[n].dx = dx;
	 diff[n].dy = dxx;
	 diff[n].p = pn;
