void skein256_2r(uint32_t X[4])
{
  uint32_t R_256_0_0 = 7;
  uint32_t R_256_0_1=  9;
  uint32_t R_256_1_0 = 26;
  uint32_t R_256_1_1 = 28;

  // MIX 0/0
  X[0] = ADD(X[0], X[1]); 
  X[1] = LROT(X[1], R_256_0_0); 
  X[1] = XOR(X[1], X[0]);

  // MIX 0/1
  X[2] = ADD(X[2], X[3]); 
  X[3] = LROT(X[3], R_256_0_1); 
  X[3] = XOR(X[3], X[2]);

  // MIX 1/0
  X[0] += X[3]; 
  X[3] = LROT(X[3], R_256_1_0); 
  X[3] ^= X[0];

  // MIX 1/1
  X[2] += X[1]; 
  X[1] = LROT(X[1], R_256_1_1); 
  X[1] ^= X[2];

}

