// The ARX operation: (((a + b) <<< k) ^ d) == xor(d,(rot(add(a+b),k)))
uint32_t arx(uint32_t a, uint32_t b, uint32_t d, uint32_t k)
{
	  uint32_t e;
	  e = XOR(d,ROT(ADD(a,b),k));
	  return e;
}

